CREATE TABLE IF NOT EXISTS `tabela` (
  `idTabela` int(11) NOT NULL AUTO_INCREMENT,
  `varChar` varchar(255) DEFAULT NULL,
  `text` text DEFAULT NULL,
  `date` date DEFAULT NULL,
  `integer` int(11) NOT NULL,
  `ordem` int(11) NOT NULL DEFAULT 0,
  `insertionDate` timestamp NOT NULL DEFAULT current_timestamp(),
  `arquivo` varchar(255) NOT NULL DEFAULT 'uploads/',
  PRIMARY KEY (`idTabela`)
) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=latin1;





CREATE TABLE IF NOT EXISTS `arquivos_tabela` (
  `uuid` char(36) NOT NULL,
  `caminho_arquivo` varchar(255) NOT NULL DEFAULT 'uploads/',
  `name` varchar(255) NOT NULL,
  `tabela_idTabela` int(11) NOT NULL,
  `insertionDate` timestamp NOT NULL DEFAULT current_timestamp(),
  `ordem` int(11) NOT NULL DEFAULT 0,
  PRIMARY KEY (`uuid`),
  KEY `fk_arquivos_tabela_tabela_idx` (`tabela_idTabela`),
  CONSTRAINT `fk_arquivos_tabela_tabela` FOREIGN KEY (`tabela_idTabela`) REFERENCES `tabela` (`idTabela`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE IF NOT EXISTS `table_marota` (
  `idtable_marota` int(11) NOT NULL AUTO_INCREMENT,
  `bagaceira` varchar(255) NOT NULL,
  `fudencio` text NOT NULL,
  `puts_man` timestamp NOT NULL DEFAULT current_timestamp(),
  `insertionDate` timestamp NULL DEFAULT current_timestamp(),
  `ordem` int(11) NOT NULL DEFAULT 0,
  `tabela_idTabela` int(11) NOT NULL,
  PRIMARY KEY (`idtable_marota`),
  KEY `fk_table_marota_tabela1_idx` (`tabela_idTabela`),
  CONSTRAINT `fk_table_marota_tabela1` FOREIGN KEY (`tabela_idTabela`) REFERENCES `tabela` (`idTabela`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=latin1;

